import Particles from "react-tsparticles";
//import { loadFull } from "tsparticles";
import { loadSlim } from "tsparticles-slim";
import { useCallback, useMemo } from "react";

const ParticlesComponent = () => {
    const options = useMemo(() => {
        return {

            "fpsLimit": 60,
            "fullScreen": {
                "enable": true
            },
            "particles": {
                "number": {
                    "value": 500
                },
                "shape": {
                    "type": "circle"
                },
                "opacity": {
                    "value": 0.5
                },
                "size": {
                    "value": 400,
                    "random": {
                        "enable": true,
                        "minimumValue": 200
                    }
                },
                "move": {
                    "enable": true,
                    "speed": 10,
                    "size": true,
                    "direction": "top",
                    "outModes": {
                        "default": "out",
                        "top": "destroy",
                        "bottom": "none"
                    }
                }
            },
            "interactivity": {
                "detectsOn": "canvas",
                "events": {
                    "resize": true
                }
            },
            "detectRetina": true,
            "themes": [
                {
                    "name": "light",
                    "default": {
                        "value": true,
                        "mode": "light"
                    },
                    "options": {
                        "background": {
                            "color": "#f7f8ef"
                        },
                        "particles": {
                            "color": {
                                "value": [
                                    "#5bc0eb",
                                    "#fde74c",
                                    "#9bc53d",
                                    "#e55934",
                                    "#fa7921"
                                ]
                            }
                        }
                    }
                },
                {
                    "name": "dark",
                    "default": {
                        "value": true,
                        "mode": "dark"
                    },
                    "options": {
                        "background": {
                            "color": "#080710"
                        },
                        "particles": {
                            "color": {
                                "value": [
                                    "#004f74",
                                    "#5f5800",
                                    "#245100",
                                    "#7d0000",
                                    "#810c00"
                                ]
                            }
                        }
                    }
                }
            ],
            "emitters": {
                "position": {
                    "x": 50,
                    "y": 150
                },
                "rate": {
                    "delay": 0.2,
                    "quantity": 2
                },
                "size": {
                    "width": 100,
                    "height": 0
                }

            }
        }
    }, [])

    const particlesInit = useCallback((engine) => {
        loadSlim(engine)
    }, [])

    return <Particles init={particlesInit} options={options} />
}

export default ParticlesComponent;
