import Particles from "react-tsparticles";
//import { loadFull } from "tsparticles";
import { loadSlim } from "tsparticles-slim";
import { useCallback, useMemo } from "react";

const ParticlesComponent = () => {
    const options = useMemo(() => {
        return {
            // particles:{
            //     links:{
            //         enable:true,
            //     },
            //     move:{
            //         enable:true,
            //         speed:{min:1, max:3}
            //     },
            //     size:{
            //         value:{min:1, max:3}
            //     }
            // }
            "fullScreen": {
                "enable": true,
                "zIndex": 1
            },
            "detectRetina": true,
            "fpsLimit": 120,
            "interactivity": {
                "events": {
                    "onClick": {
                        "enable": true,
                        "mode": "push"
                    },
                    "onDiv": {
                        "elementId": "repulse-div",
                        "enable": false,
                        "mode": "repulse"
                    },
                    "onHover": {
                        "enable": true,
                        "mode": "connect",
                        "parallax": {
                            "enable": false,
                            "force": 60,
                            "smooth": 10
                        }
                    },
                    "resize": true
                },
                "modes": {
                    "bubble": {
                        "distance": 400,
                        "duration": 2,
                        "opacity": 0.8,
                        "size": 40,
                        "speed": 3
                    },
                    "connect": {
                        "distance": 80,
                        "lineLinked": {
                            "opacity": 0.5
                        },
                        "radius": 60
                    },
                    "grab": {
                        "distance": 400,
                        "lineLinked": {
                            "opacity": 1
                        }
                    },
                    "push": {
                        "quantity": 4
                    },
                    "remove": {
                        "quantity": 2
                    },
                    "repulse": {
                        "distance": 200,
                        "duration": 0.4
                    }
                }
            },
            "particles": {
                "color": {
                    "value": "random"
                },
                "lineLinked": {
                    "blink": false,
                    "color": "#ffffff",
                    "consent": false,
                    "distance": 150,
                    "enable": false,
                    "opacity": 0.4,
                    "width": 1
                },
                "move": {
                    "attract": {
                        "enable": false,
                        "rotate": {
                            "x": 600,
                            "y": 1200
                        }
                    },
                    "bounce": false,
                    "direction": "none",
                    "enable": true,
                    "outMode": "out",
                    "random": false,
                    "speed": 6,
                    "straight": false
                },
                "number": {
                    "density": {
                        "enable": true,
                        "area": 800
                    },
                    "limit": 500,
                    "value": 300
                },
                "opacity": {
                    "animation": {
                        "enable": false,
                        "minimumValue": 0.1,
                        "speed": 1,
                        "sync": false
                    },
                    "random": false,
                    "value": 0.5
                },
                "shape": {
                    "type": "circle"
                },
                "size": {
                    "animation": {
                        "enable": false,
                        "minimumValue": 0.1,
                        "speed": 40,
                        "sync": false
                    },
                    "random": {
                        "enable": true,
                        "minimumValue": 10
                    },
                    "value": 15
                }
            },
            "polygon": {
                "draw": {
                    "enable": false,
                    "lineColor": "#ffffff",
                    "lineWidth": 0.5
                },
                "move": {
                    "radius": 10
                },
                "scale": 1,
                "type": "none",
                "url": ""
            },
            "background": {
                "color": "#000000",
                "image": "",
                "position": "50% 50%",
                "repeat": "no-repeat",
                "size": "cover"
            }
        }
    }, [])

    const particlesInit = useCallback((engine) => {
        loadSlim(engine)
    }, [])

    return <Particles init={particlesInit} options={options}/>
}

export default ParticlesComponent;
