import Particles from "react-tsparticles";
//import { loadFull } from "tsparticles";
import { loadSlim } from "tsparticles-slim";
import { useCallback, useMemo } from "react";

const ParticlesComponent = () => {
    const options = useMemo(() => {
        return {
            "fullScreen": {
                "enable": true,
                "zIndex": 1
            },
            "fpsLimit": 120,
            "particles": {
                "number": {
                    "value": 30,
                    "density": {
                        "enable": false,
                        "area": 800
                    }
                },
                "color": {
                    "value": ["#5bc0eb", "#fde74c", "#9bc53d", "#e55934", "#fa7921"]
                },
                "shape": {
                    "type": "circle"
                },
                "opacity": {
                    "value": 0.8,
                    "random": {
                        "enable": true,
                        "minimumValue": 0.4
                    },
                    "animation": {
                        "enable": false,
                        "speed": 1,
                        "minimumValue": 0.1,
                        "sync": false
                    }
                },
                "size": {
                    "value": 400,
                    "random": {
                        "enable": true,
                        "minimumValue": 300
                    },
                    "animation": {
                        "enable": true,
                        "speed": 100,
                        "minimumValue": 300,
                        "sync": false
                    }
                },
                "move": {
                    "enable": true,
                    "speed": 5,
                    "direction": "top",
                    "random": false,
                    "straight": false,
                    "outModes": {
                        "default": "out"
                    }
                }
            },
            "detectRetina": true,

            
        }
    }, [])

    const particlesInit = useCallback((engine) => {
        loadSlim(engine)
    }, [])

    return <Particles init={particlesInit} options={options}/>
}

export default ParticlesComponent;
